local keywordHandler = KeywordHandler:new()
local npcHandler = NpcHandler:new(keywordHandler)
NpcSystem.parseParameters(npcHandler)

function onCreatureAppear(cid)			npcHandler:onCreatureAppear(cid)			end
function onCreatureDisappear(cid)		npcHandler:onCreatureDisappear(cid)			end
function onCreatureSay(cid, type, msg)		npcHandler:onCreatureSay(cid, type, msg)		end
function onThink()		npcHandler:onThink()		end

local voices = { {text = 'Venha até minha taverna e compartilhe algumas historias!'} }
npcHandler:addModule(VoiceModule:new(voices))

-- Basic
keywordHandler:addKeyword({'hut'}, StdModule.say, {npcHandler = npcHandler, text = "Espero que você goste. Quer algum alimento {food}?"})
keywordHandler:addKeyword({'job'}, StdModule.say, {npcHandler = npcHandler, text = "Eu sou o dono deste salão. Eu o chamo de Cabana do Frodo. Aliás, eu estou vendendo {selling} alimentos."})
keywordHandler:addAliasKeyword({'saloon'})
keywordHandler:addKeyword({'food'}, StdModule.say, {npcHandler = npcHandler, text = "Eu posso te oferecer um pão, queijo, presunto ou carne. Caso queira ver minhas ofertas, me chame para negociar {trade}."})
keywordHandler:addKeyword({'news'}, StdModule.say, {npcHandler = npcHandler, text = "Alguns aventureiros de Edron me disseram sobre um grande tesouro guardado por cruéis demônios nas masmorras daqui."})
keywordHandler:addKeyword({'king'}, StdModule.say, {npcHandler = npcHandler, text = "Oh, nosso amado Rei! Sou muito agradecido a ele, o Álcool é muito barato!"})
keywordHandler:addAliasKeyword({'tibianus'})
keywordHandler:addKeyword({'general'}, StdModule.say, {npcHandler = npcHandler, text = "Harkath Bloodblade é o royal general."})
keywordHandler:addKeyword({'army'}, StdModule.say, {npcHandler = npcHandler, text = "Hehe. Grandes clientes."})
keywordHandler:addKeyword({'name'}, StdModule.say, {npcHandler = npcHandler, text = "Apenas me chame de Frodo."})
keywordHandler:addKeyword({'time'}, StdModule.say, {npcHandler = npcHandler, text = "São exatamente |TIME|."})
keywordHandler:addKeyword({'dungeons'}, StdModule.say, {npcHandler = npcHandler, text = "Ah sim, a masmorra do cemitério. Tudo o que eu sei é esse enigma: Sua cova para o sul, a árvore abaixo, sua alma em uma sombra. Não tenho ideia do que significa!"})
keywordHandler:addAliasKeyword({'graveyard'})
keywordHandler:addKeyword({'riddle'}, StdModule.say, {npcHandler = npcHandler, text = "Eu o escutei quando era uma criança."})
keywordHandler:addKeyword({'one eyed stranger'}, StdModule.say, {npcHandler = npcHandler, text = "Sim, eu me lembro dele. Seu nome era Berfasmur."})
keywordHandler:addKeyword({'berfasmur'}, StdModule.say, {npcHandler = npcHandler, text = "Me desculpe, ele fala muito pouco. Eu não sei nada sobre ele."})
keywordHandler:addKeyword({'tibia'}, StdModule.say, {npcHandler = npcHandler, text = "Ah para! Você sabe que esse mundo se chama Tibia."})
keywordHandler:addKeyword({'excalibug'}, StdModule.say, {npcHandler = npcHandler, text = "Nada mais do que uma história sobre guerreiros."})
keywordHandler:addKeyword({'ferumbras'}, StdModule.say, {npcHandler = npcHandler, text = "Uhm, não mencione ele. Pode assustar os clientes."})
keywordHandler:addKeyword({'cropwell'}, StdModule.say, {npcHandler = npcHandler, text = "Não faço ideia de quem seja esse, mas talvez você encontre algo nos Royal Archives..."})
keywordHandler:addKeyword({'royal archives'}, StdModule.say, {npcHandler = npcHandler, text = "Oh, os Royal Archives estão no Castelo da Chuva!"})
keywordHandler:addKeyword({'rain castle'}, StdModule.say, {npcHandler = npcHandler, text = "A residência do Rei foi renovada muito tarde."})
keywordHandler:addKeyword({'carlin'}, StdModule.say, {npcHandler = npcHandler, text = "Muitos viajantes contam histórias sobre as mulheres nas cidade do norte."})
keywordHandler:addKeyword({'thais'}, StdModule.say, {npcHandler = npcHandler, text = "Thais é o centro de todo o Tibia."})
keywordHandler:addKeyword({'donald'}, StdModule.say, {npcHandler = npcHandler, text = "Ele é um pouco tímido. Quando era jovem queria se tornar um druid."})
keywordHandler:addKeyword({'baxter'}, StdModule.say, {npcHandler = npcHandler, text = "Ele consegue beber um drink ou dois."})
keywordHandler:addKeyword({'bozo'}, StdModule.say, {npcHandler = npcHandler, text = "Estou planejando contrata-lo por uma noite ou duas, quem sabe suas piadas não alegrem meus clientes."})
keywordHandler:addKeyword({'eclesius'}, StdModule.say, {npcHandler = npcHandler, text = "Oh, aquela velho homem! Ele é um ótimo cliente, disso tenho certeza."})
keywordHandler:addKeyword({'elane'}, StdModule.say, {npcHandler = npcHandler, text = "Você consegue acreditar que ela disse a seus seguidores que o Álcool é uma coisa ruim?"})
keywordHandler:addKeyword({'galuna'}, StdModule.say, {npcHandler = npcHandler, text = "Ela faz os melhores arcos e flechas, são excelentes."})
keywordHandler:addKeyword({'gorn'}, StdModule.say, {npcHandler = npcHandler, text = "Muitos de seus clientes visitam minha Cabana também."})
keywordHandler:addKeyword({'gregor'}, StdModule.say, {npcHandler = npcHandler, text = "Os guerreiros fazem festas aqui depois de lutas na arena, as vezes."})
keywordHandler:addKeyword({'harkath bloodblade'}, StdModule.say, {npcHandler = npcHandler, text = "Muito disciplinada para curtir a vida."})
keywordHandler:addKeyword({'hugo'}, StdModule.say, {npcHandler = npcHandler, text = "Eu acho que algum tempo atrás um estranho de Fibula com esse nome ficou aqui por algumas noites."})
keywordHandler:addKeyword({'lugri'}, StdModule.say, {npcHandler = npcHandler, text = "Escutei alguns boatos sobre seu eu maligno. Isso é o suficiente para que eu tenha esperânça em nunca mais encontra-lo."})
keywordHandler:addKeyword({'lungelen'}, StdModule.say, {npcHandler = npcHandler, text = "Um mago, você pode encontrá-lo em sua guild sentado perto de alguns livros - sempre!"})
keywordHandler:addKeyword({'lynda'}, StdModule.say, {npcHandler = npcHandler, text = "Isso fica apenas entre nós em: Que gatinha!"})
keywordHandler:addKeyword({'marvik'}, StdModule.say, {npcHandler = npcHandler, text = "Marvik quase nunca sai de sua guildhall."})
keywordHandler:addKeyword({'mcronald'}, StdModule.say, {npcHandler = npcHandler, text = "Os McRonalds são uma dupla muito gente boa. Donald é um amigo muito próximo."})
keywordHandler:addAliasKeyword({'sherry'})
keywordHandler:addKeyword({'muriel'}, StdModule.say, {npcHandler = npcHandler, text = "Muriel nunca nos visitou."})
keywordHandler:addKeyword({'oswald'}, StdModule.say, {npcHandler = npcHandler, text = "Eu odeio ele. Cada uma de suas visitas aqui terminam com uma briga de bar."})
keywordHandler:addKeyword({'quentin'}, StdModule.say, {npcHandler = npcHandler, text = "Ele quase não visita minha humilde taverna."})
keywordHandler:addKeyword({'samuel'}, StdModule.say, {npcHandler = npcHandler, text = "A loud neighbour, I get a lot of complaints about him."})
keywordHandler:addKeyword({'todd'}, StdModule.say, {npcHandler = npcHandler, text = "Esse cara é imundo, mas é rico. Ele alugou um quarto nos andares de cima por meses e sempre encomenda o melhor vinho e cerveja que eu sirvo."})
keywordHandler:addKeyword({'xodet'}, StdModule.say, {npcHandler = npcHandler, text = "Não sei onde ele consegue esse fluídos. Se eu pudesse vendê-los aqui, a cabana estaria sempre lotada."})

npcHandler:setMessage(MESSAGE_GREET, "Bem-vindo a Cabana do Frodo. Você já escutou as notícias {news}?")
npcHandler:setMessage(MESSAGE_FAREWELL, "Por favor, volte sempre.")
npcHandler:setMessage(MESSAGE_WALKAWAY, "EI! Não saia sem pagar, vou colocar na sua conta!")

npcHandler:addModule(FocusModule:new())
