 local keywordHandler = KeywordHandler:new()
local npcHandler = NpcHandler:new(keywordHandler)
NpcSystem.parseParameters(npcHandler)

function onCreatureAppear(cid)			npcHandler:onCreatureAppear(cid)			end
function onCreatureDisappear(cid)		npcHandler:onCreatureDisappear(cid)			end
function onCreatureSay(cid, type, msg)		npcHandler:onCreatureSay(cid, type, msg)		end
function onThink()				npcHandler:onThink()					end

-- Promotion
local node1 = keywordHandler:addKeyword({'promot'}, StdModule.say, {npcHandler = npcHandler, onlyFocus = true, text = 'Posso promover você por 20000 moedas de ouro. Você quer que eu o promova?'})
	node1:addChildKeyword({'yes'}, StdModule.promotePlayer, {npcHandler = npcHandler, cost = 20000, level = 20, text = 'Parabéns! Você agora foi promovido.'})
	node1:addChildKeyword({'no'}, StdModule.say, {npcHandler = npcHandler, onlyFocus = true, text = 'Tudo bem então, volte quando estiver pronto.', reset = true})
-- Postman
keywordHandler:addKeyword({'uniforms'}, StdModule.say, {npcHandler = npcHandler, text = 'Eu lembro sobre desses uniformes, eles tinham uma camada de camuflagem para que eles pudessem ser usados de dentro para fora também. Vou enviar algumas amostras de cores por correio para o Sr. Postner.'},
	function(player) return player:getStorageValue(Storage.postman.Mission06) == 5 end,
	function(player) player:setStorageValue(Storage.postman.Mission06, 6) end
)

keywordHandler:addKeyword({'uniforms'}, StdModule.say, {npcHandler = npcHandler, text = 'Os uniformes de nossos guardas e soldados são de qualidade sem precedentes, é claro.'})

-- Basic
keywordHandler:addKeyword({'subject'}, StdModule.say, {npcHandler = npcHandler, text = 'Eu sou a Rainha Eloise {Queen}. É meu dever reinar sobre esta maravilhosa cidade {ciy} e as ilhas {landys} do norte.'})
keywordHandler:addAliasKeyword({'job'})
keywordHandler:addKeyword({'justice'}, StdModule.say, {npcHandler = npcHandler, text = 'Nós, mulheres, tentamos trazer justiça e sabedoria para todos, mesmo para os homens.'})
keywordHandler:addKeyword({'name'}, StdModule.say, {npcHandler = npcHandler, text = 'Eu sou a rainha Eloise. Para você, é  {My Queen} ou {Your Majesty} claro '})
keywordHandler:addKeyword({'news'}, StdModule.say, {npcHandler = npcHandler, text = 'Eu não me importo com a fofoca como um homem com simplicidade faria.'})
keywordHandler:addKeyword({'tibia'}, StdModule.say, {npcHandler = npcHandler, text = 'Em breve toda a terra será governada por mulheres, finalmente!'})
keywordHandler:addAliasKeyword({'land'})
keywordHandler:addKeyword({'how', 'are', 'you'}, StdModule.say, {npcHandler = npcHandler, text = 'Obrigado, eu estou bem.'})
keywordHandler:addKeyword({'castle'}, StdModule.say, {npcHandler = npcHandler, text = 'É meu humilde domínio.'})
keywordHandler:addKeyword({'sell'}, StdModule.say, {npcHandler = npcHandler, text = 'Vender? Sua pergunta mostra que você é um membro típico do seu gênero!'})
keywordHandler:addKeyword({'god'}, StdModule.say, {npcHandler = npcHandler, text = 'Nós honramos os deuses do bem em nossa cidade justa, especialmente Crunor, é claro.'})
keywordHandler:addKeyword({'citizen'}, StdModule.say, {npcHandler = npcHandler, text = 'Todos os cidadãos de Carlin são meus assuntos. Eu os vejo mais como meus filhos, no entanto, especialmente a população masculina.'})
keywordHandler:addKeyword({'thais'}, StdModule.say, {npcHandler = npcHandler, text = 'Este animal assustou meu gato na minha última missão diplomática nesta cidade imunda.'})
keywordHandler:addKeyword({'ferumbras'}, StdModule.say, {npcHandler = npcHandler, text = 'Ele é o flagelo de todo o continente!'})
keywordHandler:addKeyword({'treasure'}, StdModule.say, {npcHandler = npcHandler, text = 'O tesouro real está escondido além do alcance de qualquer ladrão por meios mágicos.'})
keywordHandler:addKeyword({'monster'}, StdModule.say, {npcHandler = npcHandler, text = 'Vá e caçe-os! Para a rainha e o país!'})
keywordHandler:addKeyword({'help'}, StdModule.say, {npcHandler = npcHandler, text = 'Visite a igreja ou os ajudantes.'})
keywordHandler:addKeyword({'quest'}, StdModule.say, {npcHandler = npcHandler, text = 'Ligue para heróis logo que a necessidade surgir novamente.'})
keywordHandler:addAliasKeyword({'mission'})
keywordHandler:addKeyword({'gold'}, StdModule.say, {npcHandler = npcHandler, text = 'Nossa cidade é rica e prospera.'})
keywordHandler:addAliasKeyword({'money'})
keywordHandler:addAliasKeyword({'tax'})
keywordHandler:addKeyword({'sewer'}, StdModule.say, {npcHandler = npcHandler, text = 'Eu não quero falar sobre {sewers} esgotos.'})
keywordHandler:addKeyword({'dungeon'}, StdModule.say, {npcHandler = npcHandler, text = 'As masmorras são lugares onde os machos rastejam e procuram problemas.'})
keywordHandler:addKeyword({'equipment'}, StdModule.say, {npcHandler = npcHandler, text = 'Não hesite em visitar as magníficas lojas da nossa cidade.'})
keywordHandler:addAliasKeyword({'food'})
keywordHandler:addKeyword({'time'}, StdModule.say, {npcHandler = npcHandler, text = 'Não se preocupe com o tempo na presença de sua Rainha.'})
keywordHandler:addKeyword({'hero'}, StdModule.say, {npcHandler = npcHandler, text = 'Precisamos da ajuda dos heróis de vez em quando. Mesmo os machos provam ser úteis de vez em quando.'})
keywordHandler:addAliasKeyword({'adventure'})
keywordHandler:addKeyword({'collector'}, StdModule.say, {npcHandler = npcHandler, text = 'Os impostos em Carlin não são altos, mais um símbolo do que um sacrifício.'})
keywordHandler:addKeyword({'queen'}, StdModule.say, {npcHandler = npcHandler, text = 'Eu sou a Rainha, a única governante legítima do continente!'})
keywordHandler:addKeyword({'army'}, StdModule.say, {npcHandler = npcHandler, text = 'Pergunte a um dos soldados sobre isso.'})
keywordHandler:addKeyword({'enemy'}, StdModule.say, {npcHandler = npcHandler, text = 'Nossos inimigos são numerosos. Nós temos que lutar contra monstros vil e temos que observar este Rei Tolo no sul com cuidado.'})
keywordHandler:addAliasKeyword({'enemies'})
keywordHandler:addKeyword({'thais'}, StdModule.say, {npcHandler = npcHandler, text = 'Eles se atrevem a rejeitar meu reinado sobre eles!'})
keywordHandler:addAliasKeyword({'south'})
keywordHandler:addKeyword({'carlin'}, StdModule.say, {npcHandler = npcHandler, text = 'Nossa cidade não é maravilhosa? Você notou os lindos jardins nos telhados?'})
keywordHandler:addAliasKeyword({'city'})
keywordHandler:addKeyword({'shop'}, StdModule.say, {npcHandler = npcHandler, text = 'Meus assuntos mantêm muitas lojas finas. Vá e veja seus produtos.'})
keywordHandler:addKeyword({'merchant'}, StdModule.say, {npcHandler = npcHandler, text = 'Pergunte sobre eles.'})
keywordHandler:addAliasKeyword({'craftsmen'})
keywordHandler:addKeyword({'guild'}, StdModule.say, {npcHandler = npcHandler, text = 'As quatro principais guildas são os {Knight}, os {Paladin}, os {Druid} e os {Sorcerer}.'})
keywordHandler:addKeyword({'minotaur'}, StdModule.say, {npcHandler = npcHandler, text = 'Eles não haviam perturbado nossa cidade ultimamente. Eu acho que eles temem a ira de nossos Druidas.'})
keywordHandler:addKeyword({'paladin'}, StdModule.say, {npcHandler = npcHandler, text = 'Os paladins são grandes caçadores.'})
keywordHandler:addAliasKeyword({'legola'})
keywordHandler:addKeyword({'elane'}, StdModule.say, {npcHandler = npcHandler, text = 'É uma pena que o Alto Paladino não resida em Carlin.'})
keywordHandler:addKeyword({'knight'}, StdModule.say, {npcHandler = npcHandler, text = 'Os Knights de Carlin são os mais valentes.'})
keywordHandler:addAliasKeyword({'trisha'})
keywordHandler:addKeyword({'sorc'}, StdModule.say, {npcHandler = npcHandler, text = 'Os Sorcerers têm uma pequena ilha para sua guilda. Então, se eles explodem, não queimam a cidade inteira em ruínas.'})
keywordHandler:addAliasKeyword({'lea'})
keywordHandler:addKeyword({'druid'}, StdModule.say, {npcHandler = npcHandler, text = 'Os Druidas de Carlin são nossos protetores e conselheiros. Os seus poderes nos proporcionam riqueza e alimentação.'})
keywordHandler:addAliasKeyword({'padreia'})
keywordHandler:addKeyword({'good'}, StdModule.say, {npcHandler = npcHandler, text = 'Carlin é um centro das forças do bem, é claro.'})
keywordHandler:addKeyword({'evil'}, StdModule.say, {npcHandler = npcHandler, text = 'As forças do mal têm um aperto firme nesta cidade insignificante para o sul.'})
keywordHandler:addKeyword({'order'}, StdModule.say, {npcHandler = npcHandler, text = 'A ordem, Crunor dá ao mundo, é essencial para a sobrevivência.'})
keywordHandler:addKeyword({'chaos'}, StdModule.say, {npcHandler = npcHandler, text = 'O caos é comum nas regiões do sul, onde permitem que um homem reine por um reino.'})
keywordHandler:addKeyword({'excalibug'}, StdModule.say, {npcHandler = npcHandler, text = 'Um conto de homem ... isso significa absurdo, é claro.'})
keywordHandler:addKeyword({'reward'}, StdModule.say, {npcHandler = npcHandler, text = 'Se você quiser uma recompensa, vá e me traga algo que o rei tolo Tibianus quer querido!'})
keywordHandler:addKeyword({'tbi'}, StdModule.say, {npcHandler = npcHandler, text = 'Uma organização nociva, que só poderia ser criada por homens.'})
keywordHandler:addKeyword({'eremo'}, StdModule.say, {npcHandler = npcHandler, text = 'Diz-se que ele mora em uma pequena ilha perto de Edron. Talvez as pessoas lá saibam mais sobre ele.'})

npcHandler:setMessage(MESSAGE_GREET, 'Saúdo-te, meu leal sujeito {subject}.')
npcHandler:setMessage(MESSAGE_WALKAWAY, 'Até mais, |PLAYERNAME|!')

local focusModule = FocusModule:new()
focusModule:addGreetMessage('hail queen')
focusModule:addGreetMessage('salutations queen')
npcHandler:addModule(focusModule)
